CREATE TABLE "public"."todos" ("id" serial NOT NULL, "title" text, "description" text NOT NULL, "user_id" integer, "status_id" integer, "priority_id" integer, "finished_at" timestamptz NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("priority_id") REFERENCES "public"."priorities"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("status_id") REFERENCES "public"."statuses"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);
alter table "public"."todos" alter column "finished_at" drop not null;
